mapper.select:
select b.bno, b.buyer, b.pno, p.pname, p.product_img, b.bid_price, b.bid_status, b.bid_at
from (
	select bno from buy_bid where bno > 0

	order by bno desc
	limit #{pgvo.pageStart}, #{pgvo.qty}
) a left join buy_bid b on (a.bno = b.bno) 
inner join product p on (b.pno = p.pno)
where b.buyer = #{mno}
order by bid_at desc;
