mapper.select:
select *
from member
where id = #{id}
and pwd = #{pwd}

select
a.mno, id, email, nick_name, grade, del_at, report_count
from (
select mno from member where mno > 0 and del_at is null order by mno
limit #{pageStart}, #{qty}
) a left join member b
on a.mno = b.mno

select count(mno)
from report
where mno = #{mno}

select id
from member
where email = #{email}

select id, email
from member
where id = #{id} and email = #{email}

select nick_name
from member
where mno = #{mno}

mapper.update:
update member set
pwd = #{pwd}
where id = #{id} and email = #{email}

update member set
report_count = report_count +1
where mno = #{targetMno}

update member set
del_at = now()
where mno = #{mno}
