mapper.select:
select b.pno, a.qno, writer, content, mod_at, m.nick_name 
from (
	select qno from question
  	where pno = #{pno}
  	limit #{pgvo.pageStart}, #{pgvo.qty}
 ) a left join question b
 on a.qno = b.qno 
 inner join `member` m
 on b.writer = m.mno

mapper.update:
update question set
content = #{content}, mod_at = now()
where qno = #{qno}
